function Exit-WPFForm() {
    $TimerStatus.stop()
    $NotifyIcon.Visible = $False
    $form1.close()
    $form1.Dispose()
}

#region - setup
[void][System.Reflection.Assembly]::LoadWithPartialName('System.windows.forms')
$form1 = New-Object System.Windows.Forms.form
$form1.ShowInTaskbar = $false
$form1.WindowState = 'minimized'
$notifyIcon = New-Object System.Windows.Forms.NotifyIcon
#endregion

#region - timer
$TimerStatus = New-Object System.Windows.Forms.Timer
$TimerStatus.Interval = $refreshInterval
$TimerStatus.add_Tick({ Test-VPNConnection })
$TimerStatus.start()
#endregion
 
#region - context menu
$ContextMenu = New-Object System.Windows.Forms.ContextMenu
$NotifyIcon.ContextMenu = $ContextMenu
    
# option: re-connect
$MenuConnect = New-Object System.Windows.Forms.MenuItem
$MenuConnect.Text = 'Re-Connect'
$NotifyIcon.contextMenu.MenuItems.AddRange($MenuConnect)
$MenuConnect.add_Click({ rasdial.exe $profileName })
    
<# option: diagnostics
    $MenuDiag = New-Object System.Windows.Forms.MenuItem
    $MenuDiag.Text = "Diagnostics"
    $NotifyIcon.contextMenu.MenuItems.AddRange($MenuDiag)
    $MenuDiag.add_Click({ . $diagScript})
#>
    
# option: close tray icon
$MenuExit = New-Object System.Windows.Forms.MenuItem
$MenuExit.Text = 'Close Tray Icon'
$NotifyIcon.contextMenu.MenuItems.AddRange($MenuExit)
$MenuExit.add_Click({ Exit-WPFForm })
#endregion

Test-VPNConnection
[void][System.Windows.Forms.Application]::Run($form1)